import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);

    // Function to load the user profile only if the token exists and is valid
    const loadUserProfile = (token) => {
        axios
            .get('/api/profile', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
            .then((response) => {
                setUser(response.data);
            })
            .catch((err) => {
                if (err.response && err.response.status === 404) {
                    setError('Profile endpoint not found. Please check the server.');
                } else {
                    setError('Error fetching user data. Please try again later.');
                }
                console.error('Error fetching user data:', err.response ? err.response.data : err.message);
            });
    };

    // Check if the user is already logged in
    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if (token) {
            loadUserProfile(token); // Load user profile if the token exists
        }
    }, []);

    // Login function to authenticate user
    const loginUser = (email, password) => {
        return new Promise((resolve, reject) => {
            axios
                .post('/api/login', { email, password })
                .then((response) => {
                    if (response.data.token) {
                        localStorage.setItem('authToken', response.data.token);
                        loadUserProfile(response.data.token); // After login, load user profile
                        resolve(response.data.user);
                    } else {
                        reject("Login error");
                    }
                })
                .catch((err) => {
                    reject(err.response ? err.response.data.message : "Invalid username or password");
                });
        });
    };

    // Logout function
    const logoutUser = () => {
        localStorage.removeItem('authToken');
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, loginUser, logoutUser }}>
            {error && <div>{error}</div>}
            {children}
        </AuthContext.Provider>
    );
};
