import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [eventData, setEventData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [userRole, setUserRole] = useState(null); // –ù–∞—á–∏–Ω–∞–µ–º —Å null, —á—Ç–æ–±—ã –ø—Ä–æ–≤–µ—Ä–∏—Ç—å, —á—Ç–æ —Ä–æ–ª—å –∑–∞–≥—Ä—É–∂–µ–Ω–∞

    useEffect(() => {
        // –ó–∞–≥—Ä—É–∂–∞–µ–º —Ä–æ–ª—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ localStorage
        const role = localStorage.getItem('role');
        if (role === null) {
            setServerError('User role is not defined');
            return;
        }
        setUserRole(role === 'true'); // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —Å—Ç—Ä–æ–∫—É 'true' –≤ –±—É–ª–µ–≤–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ

        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/events');
                const data = await response.json();
                if (response.ok) {
                    setEventData(data);
                } else {
                    setServerError(data.message || 'Error fetching events');
                }
            } catch (error) {
                console.error('Error fetching events:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, []);

    const handleEventClick = (eventId) => {
        // –ü–µ—Ä–µ—Ö–æ–¥ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ä–æ–ª–∏
        if (userRole === true) {
            // –ï—Å–ª–∏ –∞–¥–º–∏–Ω, –ø–µ—Ä–µ—Ö–æ–¥–∏–º –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Å–æ–±—ã—Ç–∏—è
            navigate(`/edit-event/${eventId}`);
        } else if (userRole === false) {
            // –ï—Å–ª–∏ –æ–±—ã—á–Ω—ã–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å, –ø–µ—Ä–µ—Ö–æ–¥–∏–º –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É —Å–æ–±—ã—Ç–∏—è
            navigate(`/event/${eventId}`);
        } else {
            setServerError('Invalid role');
        }
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input type="text" placeholder="Search..." className="search-bar" />
                    <button className="user-icon" onClick={() => navigate('/profile')}>
                        üë§
                    </button>
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : eventData.length > 0 ? (
                        eventData.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>There are no events now</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
