import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
    const [userId, setUserId] = useState(1); // ����������� userId
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (userId) {
            setLoading(true);
            const token = localStorage.getItem('authToken');
            console.log('Token from localStorage:', token);
            axios
                .get(`/api/profile/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((response) => {
                    setProfile(response.data);
                })
                .catch((err) => {
                    setError('Failed to load profile data');
                    console.error(err);
                })
                .finally(() => setLoading(false));
        }
    }, [userId]);

    const leaveEvent = (eventId) => {
        const token = localStorage.getItem('authToken');
        axios
            .delete(`/api/events/leave`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                data: { userId, eventId },
            })
            .then(() => {
                setProfile((prevProfile) => ({
                    ...prevProfile,
                    events: prevProfile.events.filter((event) => event.id_event !== eventId),
                }));
            })
            .catch(() => {
                alert('Failed to leave event');
            });
    };

    if (!userId) {
        return <div>You are not authorized</div>;
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="profile-container">
            <h1>Profile</h1>
            <p><strong>Name:</strong> {profile?.name}</p>
            <p><strong>Surname:</strong> {profile?.surname}</p>
            <p><strong>Email:</strong> {profile?.email}</p>

            <h2>Events</h2>
            {profile?.events && profile?.events.length > 0 ? (
                <ul>
                    {profile.events.map((event) => (
                        <li key={event.id_event}>
                            <p>{event.name} - {new Date(event.date).toLocaleDateString()}</p>
                            <button onClick={() => leaveEvent(event.id_event)}>Leave</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You are not participating in any events</p>
            )}
        </div>
    );
};

export default ProfilePage;
