// context/ProfileContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const ProfileContext = createContext();

export const useProfile = () => {
    return useContext(ProfileContext);
};

export const ProfileProvider = ({ children }) => {
    const { user } = useAuth();
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (user) {
            setLoading(true);
            const token = localStorage.getItem('authToken');
            if (!token) {
                setError("Token is missing. Please log in again.");
                setLoading(false);
                return;
            }

            axios
                .get('/api/profile', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((response) => {
                    setProfile(response.data);
                })
                .catch((err) => {
                    setError('Failed to load profile data');
                    console.error('Error loading profile:', err.response ? err.response.data : err.message);
                })
                .finally(() => setLoading(false));
        }
    }, [user]);

    const leaveEvent = (eventId) => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            setError("Token is missing. Please log in again.");
            return;
        }

        axios
            .delete('/api/events/leave', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                data: { userId: user.id, eventId },
            })
            .then(() => {
                setProfile((prevProfile) => ({
                    ...prevProfile,
                    events: prevProfile.events.filter((event) => event.id_event !== eventId),
                }));
            })
            .catch(() => {
                alert('Failed to leave event');
            });
    };

    return (
        <ProfileContext.Provider value={{ profile, loading, error, leaveEvent }}>
            {children}
        </ProfileContext.Provider>
    );
};
