import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddPage.css';
import backgroundImage from '../images/background.jpg';

const AddPage = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        date: '',
        id_place: '',
        id_category: '',
        max_amount: '',
        image: '',
    });
    const [places, setPlaces] = useState([]); // ������ ����
    const [categories, setCategories] = useState([]); // ������ ���������
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    // �������� ���� � ���������
    useEffect(() => {
        const fetchPlacesAndCategories = async () => {
            try {
                const [placesResponse, categoriesResponse] = await Promise.all([
                    fetch('http://localhost:5000/api/places'),
                    fetch('http://localhost:5000/api/categories'),
                ]);

                const placesData = await placesResponse.json();
                const categoriesData = await categoriesResponse.json();

                setPlaces(placesData);
                setCategories(categoriesData);
            } catch (err) {
                console.error('Error fetching places or categories:', err);
                setError('Failed to load places or categories.');
            }
        };

        fetchPlacesAndCategories();
    }, []);

    // ���������� ��������� �����
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // ���������� �������� �����������
    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = () => {
                setFormData({ ...formData, image: reader.result });
            };
            reader.readAsDataURL(file);
        }
    };

    // ���������� �������� �����
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        if (!formData.name || !formData.description || !formData.date || !formData.id_place || !formData.id_category || !formData.max_amount) {
            setError('Please fill in all required fields.');
            setLoading(false);
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/events', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.message || 'Failed to add the event.');
                return;
            }

            navigate('/event');
        } catch (err) {
            console.error('Error submitting event:', err);
            setError('Server error. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="add-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <form className="add-form" onSubmit={handleSubmit}>
                <h1>Add Event</h1>
                {error && <p className="error-message">{error}</p>}

                <label htmlFor="name">Event Name</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="description">Description</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    required
                ></textarea>

                <label htmlFor="date">Date</label>
                <input
                    type="datetime-local"
                    id="date"
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="id_place">Place</label>
                <select
                    id="id_place"
                    name="id_place"
                    value={formData.id_place}
                    onChange={handleChange}
                    required
                >
                    <option value="">Select a place</option>
                    {places.map((place) => (
                        <option key={place.id_place} value={place.id_place}>
                            {place.name}
                        </option>
                    ))}
                </select>

                <label htmlFor="id_category">Category</label>
                <select
                    id="id_category"
                    name="id_category"
                    value={formData.id_category}
                    onChange={handleChange}
                    required
                >
                    <option value="">Select a category</option>
                    {categories.map((category) => (
                        <option key={category.id_category} value={category.id_category}>
                            {category.name}
                        </option>
                    ))}
                </select>

                <label htmlFor="max_amount">Max Participants</label>
                <input
                    type="number"
                    id="max_amount"
                    name="max_amount"
                    value={formData.max_amount}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="image">Image URL</label>
                <input
                    type="url"
                    id="image"
                    name="image"
                    value={formData.image}
                    onChange={handleChange}
                    placeholder="Enter image URL or upload a file below"
                />

                <label htmlFor="upload">Upload Image</label>
                <input type="file" id="upload" accept="image/*" onChange={handleImageUpload} />

                <button type="submit" className="submit-btn" disabled={loading}>
                    {loading ? 'Submitting...' : 'Add Event'}
                </button>
            </form>
        </div>
    );
};

export default AddPage;
