import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css';

const ProfilePage = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/profile', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                    },
                });

                const data = await response.json();
                if (response.ok) {
                    setUser(data.user);
                    setEvents(data.events);
                } else {
                    setServerError(data.message || 'Failed to fetch profile data.');
                }
            } catch (error) {
                console.error('Error fetching profile:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchProfile();
    }, []);

    const handleBackClick = () => {
        if (user?.role === true || user?.role === 1) {
            navigate('/admin');
        } else {
            navigate('/event');
        }
    };

    const handleDeleteEvent = async (eventId) => {
        console.log('Deleting event with ID:', eventId); // Log to ensure the eventId is correct
        try {
            const response = await fetch(`http://localhost:5000/api/profile/event/${eventId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                },
            });

            if (response.ok) {
                const data = await response.json();
                setEvents(events.filter(event => event.id_event !== eventId));
            } else {
                const data = await response.json();
                alert(data.message); // Display any message from the backend
            }
        } catch (error) {
            console.error('Error removing event:', error);
            alert('Server error. Please try again later.');
        }
    };

    return (
        <div className="profile-container">
            <div className="profile-info">
                
                {serverError && <p className="error">{serverError}</p>}

                {isLoading ? (
                    <p>Loading profile...</p>
                ) : user ? (
                        <>
                            <button className="back-button" onClick={handleBackClick}>
                                ⬅ Back
                            </button>
                        <h2>{user.name} {user.surname}</h2>
                        <p>Email: {user.email}</p>

                        <h3>Registered Events:</h3>
                        <ul>
                            {events.length > 0 ? events.map(event => (
                                <li key={event.id_event}>
                                    {event.event_name} - {event.event_date}
                                    <button
                                        onClick={() => handleDeleteEvent(event.id_event)}
                                        className="delete-btn">
                                        ❌
                                    </button>
                                </li>
                            )) : (
                                <p>No events registered.</p>
                            )}
                        </ul>
                    </>
                ) : (
                    <p>Failed to load user data.</p>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
