import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = ({ userId }) => {
    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await axios.get(`/api/profile/${userId}`);
                setUser(response.data.user);
                setEvents(response.data.events);
            } catch (err) {
                setError('Failed to fetch profile data.');
            }
        };

        fetchProfile();
    }, [userId]);

    const handleRemoveFromEvent = async (eventId) => {
        try {
            await axios.delete(`/api/events/${eventId}/remove`, {
                data: { userId: userId }
            });

            // ��������� ������ ������� ����� ��������
            setEvents((prevEvents) => prevEvents.filter(event => event.id_event !== eventId));
        } catch (err) {
            setError('Failed to remove from event.');
        }
    };

    return (
        <div>
            {error && <p>{error}</p>}
            {user ? (
                <div>
                    <h2>{user.name} {user.surname}</h2>
                    <p>Email: {user.email}</p>
                    <h3>Registered Events:</h3>
                    <ul>
                        {events.map((event) => (
                            <li key={event.id_event}>
                                {event.name}
                                <button onClick={() => handleRemoveFromEvent(event.id_event)}>
                                    Leave Event
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            ) : (
                <p>Loading profile...</p>
            )}
        </div>
    );
};

export default ProfilePage;
