import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [eventData, setEventData] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        date: '',
        id_place: '',
        id_category: '',
        max_amount: '',
        image: '',
    });
    const [places, setPlaces] = useState([]);
    const [categories, setCategories] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]); 
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [searchText, setSearchText] = useState(''); 


    useEffect(() => {
        const fetchPlacesAndCategories = async () => {
            try {
                const [placesResponse, categoriesResponse] = await Promise.all([
                    fetch('http://localhost:5000/api/places'),
                    fetch('http://localhost:5000/api/categories'),
                ]);

                if (!placesResponse.ok || !categoriesResponse.ok) {
                    throw new Error('Failed to fetch places or categories from the server.');
                }

                const placesData = await placesResponse.json();
                const categoriesData = await categoriesResponse.json();

                setPlaces(placesData);
                setCategories(categoriesData);
            } catch (err) {
                console.error('Error fetching places or categories:', err);
                            }
        };
        fetchPlacesAndCategories();

        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/events', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                    },
                });

                const data = await response.json();
                if (response.ok) {
                    setEventData(data); 
                    setFilteredEvents(data); 
                } else {
                    setServerError(data.message || 'Failed to fetch events data.');
                }
            } catch (error) {
                console.error('Error fetching events:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, []);

  
    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchText(value);

        
        const filtered = eventData.filter(event =>
            event.name.toLowerCase().includes(value.toLowerCase())
        );
        setFilteredEvents(filtered); 
    };

    const handleFilter = (e) => {
        const { name, value } = e.target;

        // Обновление состояния фильтров
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));

        // Фильтрация событий
        const filtered = eventData.filter((event) => {
            const categoryName = categories.find((category) => category.id_category === event.id_category)?.name || '';
            const placeName = places.find((place) => place.id_place === event.id_place)?.name || '';

            const matchesCategory = formData.id_category
                ? categoryName === value || value === ''
                : true;
            const matchesPlace = formData.id_place
                ? placeName === value || value === ''
                : true;

            return matchesCategory && matchesPlace;
        });

        setFilteredEvents(filtered);
    };



    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchText}
                        onChange={handleSearchChange} 
                    />

                   
                    <button className="user-icon" onClick={() => navigate('/profile')}>
                        👤
                    </button>
                </div>
                <div className="filters" >
                    <select
                        id="id_category"
                        name="id_category"
                        value={formData.id_category}
                        onChange={handleFilter}
                        required
                    >
                        <option value="">Select a category</option>
                        {categories.map((category) => (
                            <option key={category.id_category} value={category.id_category}>
                                {category.name}
                            </option>
                        ))}
                    </select>

                    <select
                        id="id_place"
                        name="id_place"
                        value={formData.id_place}
                        onChange={handleFilter}
                        required
                    >
                        <option value="">Select a place</option>
                        {places.map((place) => (
                            <option key={place.id_place} value={place.id_place}>
                                {place.name}
                            </option>
                        ))}
                    </select>
                </div>
                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>There are no events now</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
