import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [eventData, setEventData] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [formData, setFormData] = useState({
        id_category: '',
        id_place: '',
        date: '',
    });
    const [places, setPlaces] = useState([]);
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [placesResponse, categoriesResponse, eventsResponse] = await Promise.all([
                    fetch('http://localhost:5000/api/places'),
                    fetch('http://localhost:5000/api/categories'),
                    fetch('http://localhost:5000/api/events'),
                ]);

                if (!placesResponse.ok || !categoriesResponse.ok || !eventsResponse.ok) {
                    throw new Error('Failed to fetch data from the server.');
                }

                const [placesData, categoriesData, eventsData] = await Promise.all([
                    placesResponse.json(),
                    categoriesResponse.json(),
                    eventsResponse.json(),
                ]);

                setPlaces(placesData);
                setCategories(categoriesData);
                setEventData(eventsData);
                setFilteredEvents(eventsData); // Initialize with all events
            } catch (err) {
                console.error('Error fetching data:', err);
                setServerError('Error fetching data. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;

        // Update filter state
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    useEffect(() => {
        // Apply filters whenever formData changes
        const filtered = eventData.filter((event) => {
            const categoryName = categories.find((cat) => cat.id_category === event.id_category)?.name || '';
            const placeName = places.find((place) => place.id_place === event.id_place)?.name || '';

            const matchesCategory = !formData.id_category || categoryName === formData.id_category;
            const matchesPlace = !formData.id_place || placeName === formData.id_place;
            const matchesDate = !formData.date || event.date === formData.date;

            return matchesCategory && matchesPlace && matchesDate;
        });

        setFilteredEvents(filtered);
    }, [formData, eventData, categories, places]);

    const handleClearFilters = () => {
        setFormData({
            id_category: '',
            id_place: '',
            date: '',
        });
    };

    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        onChange={(e) =>
                            setFilteredEvents(
                                eventData.filter((event) =>
                                    event.name.toLowerCase().includes(e.target.value.toLowerCase())
                                )
                            )
                        }
                    />
                    <button className="user-icon" onClick={() => navigate('/profile')}>ðŸ‘¤</button>
                </div>

                <div className="filters">
                    <select
                        id="id_category"
                        name="id_category"
                        value={formData.id_category}
                        onChange={handleFilterChange}
                    >
                        <option value="">Select a category</option>
                        {categories.map((category) => (
                            <option key={category.id_category} value={category.name}>
                                {category.name}
                            </option>
                        ))}
                    </select>

                    <select
                        id="id_place"
                        name="id_place"
                        value={formData.id_place}
                        onChange={handleFilterChange}
                    >
                        <option value="">Select a place</option>
                        {places.map((place) => (
                            <option key={place.id_place} value={place.name}>
                                {place.name}
                            </option>
                        ))}
                    </select>

                    <input
                        type="date"
                        id="date"
                        name="date"
                        value={formData.date}
                        onChange={handleFilterChange}
                    />

                    <button onClick={handleClearFilters}>Clear Filters</button>
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No events found.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
