import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './EventDetailPage.css';
import backgroundImage from '../images/background.jpg';

const EventDetailPage = () => {
    const { eventId } = useParams();
    const [event, setEvent] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [userId, setUserId] = useState(1);
    const [eventData, setEventData] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [formData, setFormData] = useState({
        id_category: '',
        id_place: '',
        date: '',
    });
    const [places, setPlaces] = useState([]);
    const [categories, setCategories] = useState([]);

    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    useEffect(() => {
        const fetchEvent = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`http://localhost:5000/api/events/${eventId}`);
                const data = await response.json();

                console.log('Fetched event details:', data); 

                if (response.ok) {
                    const eventData = {
                        ...data,
                        participants: data.participants || [],
                    };
                    setEventData(eventData); 
                } else {
                    setServerError(data.message || 'Error fetching event details');
                }
            } catch (error) {
                console.error('Error fetching event:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvent();
    }, [eventId]);

    const handleRegisterClick = async () => {
        if (!userId) {
            alert('Please log in first!');
            return;
        }

        if (event.max_amount && event.participants.length >= event.max_amount) {
            alert('Sorry, the event is full!');
            return;
        }

        try {
            const response = await fetch(`http://localhost:5000/api/events/${eventId}/register`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userId }),
            });

            const data = await response.json();
            if (response.ok) {
                alert(data.message);
                setEvent((prevEvent) => ({
                    ...prevEvent,
                    participants: [...(prevEvent.participants || []), { id: userId, name: 'Your Name' }],
                }));
            } else {
                alert(data.message || 'Registration failed');
            }
        } catch (error) {
            console.error('Error registering for event:', error);
            alert('Server error during registration');
        }
    };

    if (isLoading) {
        return <p>Loading event details...</p>;
    }

    if (!event) {
        return <p>Event not found</p>;
    }

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="event-detail-container">
                {serverError && <p className="error">{serverError}</p>}

                <div className="event-detail">
                    <button className="back-button" onClick={() => window.history.back()}>Back</button>

                    <img src={event.image || 'default-image.jpg'} alt={event.name} className="event-image" />
                    <div className="event-info">
                        <h1>{event.name}</h1>
                        <p>{event.description}</p>
                        <p>Date: {new Date(event.date).toLocaleString()}</p>
                        <p>Place: {event.place_name}</p>
                        <p>Category: {event.category_name}</p>

                        {/* Обработка отображения максимального числа участников */}
                        <p>Maximum Participants: {event.max_amount != null ? event.max_amount : 'No limit'}</p>

                        <h3>Participants ({event.participants.length}{event.max_amount != null ? `/${event.max_amount}` : ''}):</h3>
                        <ul>
                            {event.participants.length > 0 ? (
                                event.participants.map((participant, index) => (
                                    <li key={index}>
                                        {participant.name} {participant.surname}
                                    </li>
                                ))
                            ) : (
                                <p>No participants yet.</p>
                            )}
                        </ul>

                        <button
                            onClick={handleRegisterClick}
                            disabled={event.max_amount && event.participants.length >= event.max_amount}
                        >
                            {event.max_amount && event.participants.length >= event.max_amount
                                ? 'Event Full'
                                : 'Register for Event'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EventDetailPage;
