import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [categories, setCategories] = useState([]);
    const [locations, setLocations] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [searchText, setSearchText] = useState('');
    const [selectedFilters, setSelectedFilters] = useState({
        category: '',
        date: '',
        location: ''
    });

    useEffect(() => {
        // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð´Ð»Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð² Ð¿Ñ€Ð¸ Ð¼Ð¾Ð½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
        fetchFiltersData();
    }, []);

    useEffect(() => {
        fetchFilteredEvents();
    }, [searchText, selectedFilters]);

    const fetchFiltersData = async () => {
        try {
            const [categoriesResponse, locationsResponse] = await Promise.all([
                fetch('http://localhost:5000/api/events/categories', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                    },
                }),
                fetch('http://localhost:5000/api/events/locations', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                    },
                })
            ]);

            if (categoriesResponse.ok && locationsResponse.ok) {
                setCategories(await categoriesResponse.json());
                setLocations(await locationsResponse.json());
            } else {
                setServerError('Failed to load filter data.');
            }
        } catch (error) {
            console.error('Error fetching filter data:', error);
            setServerError('Server error. Please try again later.');
        }
    };

    const fetchFilteredEvents = async () => {
        setIsLoading(true);
        setServerError('');
        try {
            // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ query Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹ Ð´Ð»Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ð¸
            const queryParams = new URLSearchParams({
                search: searchText,
                category: selectedFilters.category || '',
                date: selectedFilters.date || '',
                location: selectedFilters.location || ''
            }).toString();

            // Ð—Ð°Ð¿Ñ€Ð°ÑˆÐ¸Ð²Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ñ ÑÐµÑ€Ð²ÐµÑ€Ð¾Ð¼
            const response = await fetch(`http://localhost:5000/api/events?${queryParams}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                },
            });

            const data = await response.json();
            if (response.ok) {
                setFilteredEvents(data); // Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð½Ñ‹Ðµ ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ñ
            } else {
                setServerError(data.message || 'Failed to fetch events.');
            }
        } catch (error) {
            console.error('Error fetching events:', error);
            setServerError('Server error. Please try again later.');
        } finally {
            setIsLoading(false);
        }
    };


    const handleSearchChange = (e) => {
        setSearchText(e.target.value);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setSelectedFilters((prevFilters) => ({
            ...prevFilters,
            [name]: value
        }));
    };

    const handleResetFilters = () => {
        setSelectedFilters({
            category: '',
            date: '',
            location: ''
        });
        setSearchText('');
    };

    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchText}
                        onChange={handleSearchChange}
                    />
                    <button className="user-icon" onClick={() => navigate('/profile')}>
                        ðŸ‘¤
                    </button>
                </div>

                <div className="filters">
                    <select name="category" onChange={handleFilterChange} value={selectedFilters.category}>
                        <option value="">All Categories</option>
                        {categories.map((category) => (
                            <option key={category.id} value={category.name}>{category.name}</option>
                        ))}
                    </select>
                    <select name="date" onChange={handleFilterChange} value={selectedFilters.date}>
                        <option value="">All Dates</option>
                        <option value="today">Today</option>
                        <option value="this_week">This Week</option>
                        <option value="this_month">This Month</option>
                    </select>
                    <select name="location" onChange={handleFilterChange} value={selectedFilters.location}>
                        <option value="">All Locations</option>
                        {locations.map((location) => (
                            <option key={location.id} value={location.name}>{location.name}</option>
                        ))}
                    </select>
                    <button className="reset-button" onClick={handleResetFilters}>
                        ðŸ”„
                    </button>
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{new Date(event.date).toLocaleDateString()}</p>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No events match the selected filters.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
