import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [searchText, setSearchText] = useState('');
    const [selectedFilters, setSelectedFilters] = useState({
        category: '',
        date: '',
        location: '',
        organizer: ''
    });

    useEffect(() => {
        fetchFilteredEvents();
    }, [searchText, selectedFilters]);

    const fetchFilteredEvents = async () => {
        setIsLoading(true);
        setServerError('');
        try {
            const queryParams = new URLSearchParams({
                search: searchText,
                category: selectedFilters.category || '',
                date: selectedFilters.date || '',
                location: selectedFilters.location || '',
                organizer: selectedFilters.organizer || ''
            }).toString();

            const response = await fetch(`http://localhost:5000/api/events?${queryParams}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                },
            });

            const data = await response.json();
            if (response.ok) {
                setFilteredEvents(data);
            } else {
                setServerError(data.message || 'Failed to fetch events.');
            }
        } catch (error) {
            console.error('Error fetching events:', error);
            setServerError('Server error. Please try again later.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleSearchChange = (e) => {
        setSearchText(e.target.value);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setSelectedFilters((prevFilters) => ({
            ...prevFilters,
            [name]: value
        }));
    };

    const handleResetFilters = () => {
        setSelectedFilters({
            category: '',
            date: '',
            location: '',
            organizer: ''
        });
        setSearchText('');
    };

    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchText}
                        onChange={handleSearchChange}
                    />
                    <button className="user-icon" onClick={() => navigate('/profile')}>
                        ðŸ‘¤
                    </button>
                </div>

                <div className="filters">
                    <select name="category" onChange={handleFilterChange} value={selectedFilters.category}>
                        <option value="">All Categories</option>
                        <option value="music">Music</option>
                        <option value="sports">Sports</option>
                        <option value="art">Art</option>
                        {/* Add more categories dynamically if required */}
                    </select>
                    <select name="date" onChange={handleFilterChange} value={selectedFilters.date}>
                        <option value="">All Dates</option>
                        <option value="today">Today</option>
                        <option value="this_week">This Week</option>
                        <option value="this_month">This Month</option>
                    </select>
                    <select name="location" onChange={handleFilterChange} value={selectedFilters.location}>
                        <option value="">All Locations</option>
                        <option value="nyc">New York City</option>
                        <option value="la">Los Angeles</option>
                        {/* Add more locations dynamically if required */}
                    </select>
                    <select name="organizer" onChange={handleFilterChange} value={selectedFilters.organizer}>
                        <option value="">All Organizers</option>
                        <option value="org1">Organizer 1</option>
                        <option value="org2">Organizer 2</option>
                    </select>
                    <button className="reset-button" onClick={handleResetFilters}>
                        ðŸ”„
                    </button>
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{new Date(event.date).toLocaleDateString()}</p>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No events match the selected filters.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
