import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [eventData, setEventData] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [formData, setFormData] = useState({
        id_category: '',
        id_place: '',
        date: '',
    });
    const [places, setPlaces] = useState([]);
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');

    // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ Ð¸ Ð¼ÐµÑÑ‚Ð°
    useEffect(() => {
        const fetchData = async () => {
            try {
                const [placesResponse, categoriesResponse] = await Promise.all([
                    fetch('http://localhost:5000/api/places'),
                    fetch('http://localhost:5000/api/categories'),
                ]);

                if (!placesResponse.ok || !categoriesResponse.ok) {
                    throw new Error('Failed to fetch places or categories.');
                }

                const [placesData, categoriesData] = await Promise.all([
                    placesResponse.json(),
                    categoriesResponse.json(),
                ]);

                setPlaces(placesData);
                setCategories(categoriesData);
            } catch (err) {
                console.error('Error fetching places or categories:', err);
                setServerError('Error fetching places or categories.');
            }
        };

        fetchData();
    }, []);

    // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ñ Ð¿Ñ€Ð¸ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ð¸ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²
    useEffect(() => {
        const fetchEvents = async () => {
            const { id_category, id_place, date } = formData;
            const queryParams = new URLSearchParams();

            if (id_category) queryParams.append('category', id_category);
            if (id_place) queryParams.append('place', id_place);
            if (date) queryParams.append('date', date);

            console.log('Request Params:', queryParams.toString());

            try {
                const response = await fetch(`http://localhost:5000/api/events?${queryParams.toString()}`);
                if (response.ok) {
                    const eventsData = await response.json();
                    console.log('Received Events Data:', eventsData);
                    setEventData(eventsData);
                    setFilteredEvents(eventsData);
                } else {
                    setServerError('Failed to fetch events.');
                }
            } catch (err) {
                console.error('Error fetching events:', err);
                setServerError('Error fetching events. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, [formData]); // ÐŸÑ€Ð¸ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ð¸ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð² Ð¾Ð±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¿Ð¸ÑÐ¾Ðº ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ð¹

    // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²
    const handleFilter = (e) => {
        const { name, value } = e.target;

        // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°
        setFormData(prev => ({
            ...prev,
            [name]: value,
        }));

        // Ð¤Ð¾Ñ€Ð¼Ð¸Ñ€ÑƒÐµÐ¼ Ð·Ð°Ð¿Ñ€Ð¾Ñ Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ð¼Ð¸
        let query = '';
        if (formData.id_place) query += `&place=${formData.id_place}`;
        if (formData.id_category) query += `&category=${formData.id_category}`;
        if (formData.date) query += `&date=${formData.date}`;

        // Ð’Ñ‹Ð¿Ð¾Ð»Ð½ÑÐµÐ¼ Ð·Ð°Ð¿Ñ€Ð¾Ñ Ñ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð°Ð¼Ð¸
        fetch(`http://localhost:5000/api/events?${query}`)
            .then(response => response.json())
            .then(data => setFilteredEvents(data)) // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð¾Ñ‚Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ðµ ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ñ
            .catch(err => console.error('Error fetching events:', err));
    };


    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        onChange={(e) =>
                            setFilteredEvents(
                                eventData.filter((event) =>
                                    event.name.toLowerCase().includes(e.target.value.toLowerCase())
                                )
                            )
                        }
                    />
                    <button className="user-icon" onClick={() => navigate('/profile')}>ðŸ‘¤</button>
                </div>

                <div className="filters">
                    <select
                        id="id_category"
                        name="id_category"
                        value={formData.id_category}
                        onChange={handleFilterChange}
                    >
                        <option value="">Select a category</option>
                        {categories.map((category) => (
                            <option key={category.id_category} value={category.id_category}>
                                {category.name}
                            </option>
                        ))}
                    </select>

                    <select
                        id="id_place"
                        name="id_place"
                        value={formData.id_place}
                        onChange={handleFilterChange}
                    >
                        <option value="">Select a place</option>
                        {places.map((place) => (
                            <option key={place.id_place} value={place.id_place}>
                                {place.name}
                            </option>
                        ))}
                    </select>

                    <input
                        type="date"
                        id="date"
                        name="date"
                        value={formData.date}
                        onChange={handleFilterChange}
                    />
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No events found.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
