import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [eventData, setEventData] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [searchText, setSearchText] = useState('');

    // State for filter options
    const [category, setCategory] = useState('');
    const [place, setPlace] = useState('');
    const [date, setDate] = useState('');

    useEffect(() => {
        const fetchEvents = async () => {
            const params = new URLSearchParams();

            // Add query parameters for filters if they are set
            if (searchText) params.append('search', searchText);
            if (category) params.append('category', category);
            if (place) params.append('place', place);
            if (date) params.append('date', date);

            try {
                const response = await fetch(`http://localhost:5000/api/events?${params.toString()}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                    },
                });

                const data = await response.json();
                if (response.ok) {
                    setEventData(data);
                    setFilteredEvents(data);
                } else {
                    setServerError(data.message || 'Failed to fetch events data.');
                }
            } catch (error) {
                console.error('Error fetching events:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, [searchText, category, place, date]); // Add dependencies for filter changes

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchText(value); // Set the search text
    };

    const handleCategoryChange = (e) => {
        setCategory(e.target.value); // Set the category filter
    };

    const handlePlaceChange = (e) => {
        setPlace(e.target.value); // Set the place filter
    };

    const handleDateChange = (e) => {
        setDate(e.target.value); // Set the date filter
    };

    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchText}
                        onChange={handleSearchChange}
                    />

                    <select className="filter-select" value={category} onChange={handleCategoryChange}>
                        <option value="">Select Category</option>
                        <option value="Music">Music</option>
                        <option value="Sports">Sports</option>
                        <option value="Technology">Technology</option>
                        <option value="Arts">Arts</option>
                    </select>

                    <select className="filter-select" value={place} onChange={handlePlaceChange}>
                        <option value="">Select Place</option>
                        <option value="New York">New York</option>
                        <option value="Los Angeles">Los Angeles</option>
                        <option value="San Francisco">San Francisco</option>
                        <option value="Chicago">Chicago</option>
                    </select>

                    <input
                        type="date"
                        className="filter-select"
                        value={date}
                        onChange={handleDateChange}
                    />

                    <button className="user-icon" onClick={() => navigate('/profile')}>
                        ðŸ‘¤
                    </button>
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>There are no events now</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
