import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './ProfilePage.css';

const ProfilePage = () => {
    const { login } = useAuth();
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (login) {
            setLoading(true);
            axios
                .get('/api/profile', { params: { userId: login.id } })
                .then((response) => {
                    console.log('Profile data:', response.data);
                    setProfile(response.data);
                })
                .catch((err) => {
                    console.error('Error fetching profile:', err);
                    setError('Failed to fetch profile data.');
                })
                .finally(() => setLoading(false));
        }
    }, [login]);

    const leaveEvent = (eventId) => {
        console.log('Leaving event:', { userId: login.id, eventId });
        axios
            .delete('/api/events/leave', { data: { userId: login.id, eventId } })
            .then(() => {
                setProfile((prevProfile) => ({
                    ...prevProfile,
                    events: prevProfile.events.filter(event => event.id_event !== eventId),
                }));
            })
            .catch(() => {
                alert('Failed to leave the event.');
            });
    };

    if (!login) {
        return <div>You are not authorized</div>;
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Profile</h1>
            <p>First Name: {profile.name}</p>
            <p>Last Name: {profile.surname}</p>
            <p>Email: {profile.email}</p>
            <h2>Events</h2>
            {profile.events && profile.events.length > 0 ? (
                <ul>
                    {profile.events.map((event) => (
                        <li key={event.id_event}>
                            <p>{event.name} - {new Date(event.date).toLocaleDateString()}</p>
                            <button onClick={() => leaveEvent(event.id_event)}>Leave</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You are not participating in any events.</p>
            )}
        </div>
    );
};

export default ProfilePage;
