import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const ProfilePage = () => {
    const { login } = useAuth();
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (login) {
            axios
                .get('/api/profile', { params: { userId: login.id } })
                .then((response) => {
                    setProfile(response.data);
                    setLoading(false);
                })
                .catch((err) => {
                    setError('Failed to fetch profile data.');
                    setLoading(false);
                });
        }
    }, [login]);

    const leaveEvent = (eventId) => {
        axios
            .delete('/api/events/leave', { data: { userId: login.id, eventId } })
            .then(() => {
                setProfile((prevProfile) => ({
                    ...prevProfile,
                    events: prevProfile.events.filter(event => event.id_event !== eventId),
                }));
            })
            .catch(() => {
                alert('Failed to leave the event.');
            });
    };

    if (!login) {
        return <div>�� �� ������������</div>;
    }

    if (loading) {
        return <div>��������...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>�������</h1>
            <p>���: {profile.name}</p>
            <p>�������: {profile.surname}</p>
            <p>Email: {profile.email}</p>
            <h2>�����������</h2>
            {profile.events.length > 0 ? (
                <ul>
                    {profile.events.map((event) => (
                        <li key={event.id_event}>
                            <p>{event.name} - {new Date(event.date).toLocaleDateString()}</p>
                            <button onClick={() => leaveEvent(event.id_event)}>�����</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>�� �� ���������� � ������������.</p>
            )}
        </div>
    );
};

export default ProfilePage;
