// pages/ProfilePage.js
import React from 'react';
import { useProfile } from '../context/ProfileContext';
import { useAuth } from '../context/AuthContext';
import './ProfilePage.css';

const ProfilePage = () => {
    const { user, logoutUser } = useAuth();
    const { profile, loading, error, leaveEvent } = useProfile();

    if (!user) {
        return <div>You are not authorized</div>;
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="profile-container">
            <h1>Profile</h1>
            <p><strong>Name:</strong> {profile?.name}</p>
            <p><strong>Surname:</strong> {profile?.surname}</p>
            <p><strong>Email:</strong> {profile?.email}</p>

            <h2>Events</h2>
            {profile?.events && profile?.events.length > 0 ? (
                <ul>
                    {profile.events.map((event) => (
                        <li key={event.id_event}>
                            <p>{event.name} - {new Date(event.date).toLocaleDateString()}</p>
                            <button onClick={() => leaveEvent(event.id_event)}>Leave</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You are not participating in any events</p>
            )}

            <button className="logout-btn" onClick={logoutUser}>Log out</button>
        </div>
    );
};

export default ProfilePage;
