import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if (token) {
            axios
                .get('/api/profile', {  // Ensure this endpoint exists on your server
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((response) => {
                    setUser(response.data);  // Assuming 'response.data' contains user data
                })
                .catch((err) => {
                    setError(err.response ? err.response.data.message : 'Failed to fetch user data');
                    console.error('Error fetching user data:', err.response ? err.response.data : err.message);
                });
        }
    }, []);

    const loginUser = (email, password) => {
        return new Promise((resolve, reject) => {
            axios
                .post('/api/login', { email, password })
                .then((response) => {
                    if (response.data.token) {
                        localStorage.setItem('authToken', response.data.token);
                        setUser(response.data.user);  // Assuming 'response.data.user' contains the user data
                        resolve(response.data.user);
                    } else {
                        reject('Login error');
                    }
                })
                .catch((err) => {
                    reject(err.response ? err.response.data.message : 'Invalid username or password');
                });
        });
    };

    const logoutUser = () => {
        localStorage.removeItem('authToken');
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, loginUser, logoutUser }}>
            {error && <div>{error}</div>}  {/* Display the error message in the UI */}
            {children}
        </AuthContext.Provider>
    );
};
