import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background.jpg';

const EventsPage = () => {
    const navigate = useNavigate();
    const [eventData, setEventData] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [searchText, setSearchText] = useState('');
    const [selectedFilters, setSelectedFilters] = useState({
        category: '',
        date: '',
        location: '',
        organizer: ''
    });

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/events', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                    },
                });

                const data = await response.json();
                if (response.ok) {
                    setEventData(data);
                    setFilteredEvents(data);
                } else {
                    setServerError(data.message || 'Failed to fetch events data.');
                }
            } catch (error) {
                console.error('Error fetching events:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchText(value);
        filterEvents(value, selectedFilters);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        const updatedFilters = { ...selectedFilters, [name]: value };
        setSelectedFilters(updatedFilters);
        filterEvents(searchText, updatedFilters);
    };

    const filterEvents = (search, filters) => {
        const filtered = eventData.filter(event => {
            const matchesSearch = event.name.toLowerCase().includes(search.toLowerCase());
            const matchesCategory = filters.category ? event.category === filters.category : true;
            const matchesDate = filters.date ? event.date === filters.date : true;
            const matchesLocation = filters.location ? event.location === filters.location : true;
            const matchesOrganizer = filters.organizer ? event.organizer === filters.organizer : true;

            return matchesSearch && matchesCategory && matchesDate && matchesLocation && matchesOrganizer;
        });
        setFilteredEvents(filtered);
    };

    const handleResetFilters = () => {
        setSelectedFilters({
            category: '',
            date: '',
            location: '',
            organizer: ''
        });
        filterEvents(searchText, {
            category: '',
            date: '',
            location: '',
            organizer: ''
        });
    };

    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="container">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search..."
                        className="search-bar"
                        value={searchText}
                        onChange={handleSearchChange}
                    />
                    <button className="user-icon" onClick={() => navigate('/profile')}>
                        ðŸ‘¤
                    </button>
                </div>

                <div className="filters">
                    <select name="category" onChange={handleFilterChange} value={selectedFilters.category}>
                        <option value="">All Categories</option>
                        {eventData.map((event, index) => (
                            event.category && <option key={index} value={event.category}>{event.category}</option>
                        ))}
                    </select>
                    <select name="date" onChange={handleFilterChange} value={selectedFilters.date}>
                        <option value="">All Dates</option>
                        {eventData.map((event, index) => (
                            event.date && <option key={index} value={event.date}>{event.date}</option>
                        ))}
                    </select>
                    <select name="location" onChange={handleFilterChange} value={selectedFilters.location}>
                        <option value="">All Locations</option>
                        {eventData.map((event, index) => (
                            event.location && <option key={index} value={event.location}>{event.location}</option>
                        ))}
                    </select>
                    <select name="organizer" onChange={handleFilterChange} value={selectedFilters.organizer}>
                        <option value="">All Organizers</option>
                        {eventData.map((event, index) => (
                            event.organizer && <option key={index} value={event.organizer}>{event.organizer}</option>
                        ))}
                    </select>
                    <button onClick={handleResetFilters}>Reset Filters</button>
                </div>

                {serverError && <p className="error">{serverError}</p>}

                <div className="event-cards">
                    {isLoading ? (
                        <p>Loading events...</p>
                    ) : filteredEvents.length > 0 ? (
                        filteredEvents.map((event) => (
                            <div
                                className="card"
                                key={event.id_event}
                                onClick={() => handleEventClick(event.id_event)}
                            >
                                <img src={event.image} alt={event.name} />
                                <div className="card-info">
                                    <h3>{event.name}</h3>
                                    <p>{event.description}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No events match the selected filters.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventsPage;
