import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = () => {
    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId'); // �������� userId �� localStorage

    useEffect(() => {
        if (!userId) {
            setError('User ID is not provided.');
            return;
        }

        const fetchProfile = async () => {
            try {
                const response = await axios.get(`/api/profile/${userId}`);
                setUser(response.data.user);
                setEvents(response.data.events);
                setError('');
            } catch (err) {
                setError('Failed to fetch profile data.');
            }
        };

        fetchProfile();
    }, [userId]);

    const handleRemoveFromEvent = async (eventId) => {
        try {
            await axios.delete(`/api/events/${eventId}/remove`, {
                data: { userId },
            });

            setEvents((prevEvents) => prevEvents.filter((event) => event.id_event !== eventId));
        } catch (err) {
            setError('Failed to remove from event.');
        }
    };

    if (error) {
        return <p style={{ color: 'red' }}>{error}</p>;
    }

    if (!user) {
        return <p>Loading profile...</p>;
    }

    return (
        <div>
            <h2>{user.name} {user.surname}</h2>
            <p>Email: {user.email}</p>
            <h3>Registered Events:</h3>
            <ul>
                {events.map((event) => (
                    <li key={event.id_event}>
                        {event.name}
                        <button onClick={() => handleRemoveFromEvent(event.id_event)}>
                            Leave Event
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProfilePage;
