import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
    const [login, setLogin] = useState(null);
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const storedLogin = localStorage.getItem('login');
        if (storedLogin) {
            setLogin(JSON.parse(storedLogin));
        }
    }, []);

    useEffect(() => {
        if (login && login.token) {
            setLoading(true);
            axios
                .get('/api/profile', { headers: { Authorization: `Bearer ${login.token}` } })
                .then((response) => {
                    setProfile(response.data);
                })
                .catch(() => {
                    setError('Failed to fetch profile data.');
                })
                .finally(() => setLoading(false));
        }
    }, [login]);

    if (!login || !login.token) {
        return <div>You are not authorized</div>;
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Profile</h1>
            <p>Name: {profile.name}</p>
            <p>Surname: {profile.surname}</p>
            <p>Email: {profile.email}</p>
            <h2>Events</h2>
            {profile.events && profile.events.length > 0 ? (
                <ul>
                    {profile.events.map((event) => (
                        <li key={event.id_event}>
                            <p>{event.name} - {new Date(event.date).toLocaleDateString()}</p>
                            <button onClick={() => leaveEvent(event.id_event)}>Leave</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You are not participating in any events.</p>
            )}
        </div>
    );
};

export default ProfilePage;
