import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventsPage.css';
import backgroundImage from '../images/background-image.jpg'; 

const RegisterPage = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        confirmPassword: '',
    });

    const [errors, setErrors] = useState({});
    const [serverError, setServerError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.firstName.trim()) newErrors.firstName = 'Name is required';
        if (!formData.lastName.trim()) newErrors.lastName = 'Surname is required';
        if (!formData.email.trim() || !/\S+@\S+\.\S+/.test(formData.email))
            newErrors.email = 'Enter a valid email';
        if (!formData.password.trim() || formData.password.length < 6)
            newErrors.password = 'Password must be at least 6 characters';
        if (formData.password !== formData.confirmPassword)
            newErrors.confirmPassword = 'Passwords do not match';
        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const validationErrors = validateForm();
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/events', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    firstName: formData.firstName,
                    lastName: formData.lastName,
                    email: formData.email,
                    password: formData.password,
                }),
            });

            const data = await response.json();
            if (!response.ok) {
                setServerError(data.message || 'Server error. Please try again later.');
                return;
            }

            navigate('/event');
        } catch (error) {
            console.error('Error during registration:', error);
            setServerError('Server error. Please try again later.');
        }
    };

    return (
        <div className="background">
            <div className="container">
                <div className="top-bar">
                    <input type="text" placeholder="Search..." className="search-bar" />
                    <button className="user-icon" onClick={() => navigate('/user')}>
                        ðŸ‘¤
                    </button>
                </div>

                <h1>Registration</h1>
                {serverError && <p className="error">{serverError}</p>}

                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Name:</label>
                        <input
                            type="text"
                            name="firstName"
                            value={formData.firstName}
                            onChange={handleChange}
                        />
                        {errors.firstName && <span className="error">{errors.firstName}</span>}
                    </div>
                    <div className="form-group">
                        <label>Surname:</label>
                        <input
                            type="text"
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleChange}
                        />
                        {errors.lastName && <span className="error">{errors.lastName}</span>}
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                        />
                        {errors.email && <span className="error">{errors.email}</span>}
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                        />
                        {errors.password && <span className="error">{errors.password}</span>}
                    </div>
                    <div className="form-group">
                        <label>Confirm password:</label>
                        <input
                            type="password"
                            name="confirmPassword"
                            value={formData.confirmPassword}
                            onChange={handleChange}
                        />
                        {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
                    </div>
                    <button type="submit">Register</button>
                    <button type="button" onClick={() => navigate('/')}>
                        Back
                    </button>
                </form>

                <div className="event-cards">
                    <div className="card">
                        <img src="event-image.jpg" alt="Event" />
                        <div className="card-info">
                            <h3>Event Title</h3>
                            <p>Short event description...</p>
                        </div>
                    </div>
                    <div className="card">
                        <img src="event-image2.jpg" alt="Event" />
                        <div className="card-info">
                            <h3>Event Title 2</h3>
                            <p>Another short event description...</p>
                        </div>
                    </div>
                    {/* Ð”Ð¾Ð±Ð°Ð²ÑŒ Ð´Ñ€ÑƒÐ³Ð¸Ðµ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸ Ð¿Ð¾ Ð°Ð½Ð°Ð»Ð¾Ð³Ð¸Ð¸ */}
                </div>
            </div>
        </div>
    );
};

export default RegisterPage;
