import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { ProfileProvider } from './context/ProfileContext';
import './ProfilePage.css';

const ProfilePage = () => {
    const { user, logoutUser } = ProfileProvider();
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (user) {
            setLoading(true);
            const token = localStorage.getItem('authToken');
            if (!token) {
                setError("Token is missing. Please log in again.");
                setLoading(false);
                return;
            }

            axios
                .get('/api/profile', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((response) => {
                    setProfile(response.data);
                })
                .catch((err) => {
                    setError('Failed to load profile data');
                    console.error('Error loading profile:', err.response ? err.response.data : err.message);
                })
                .finally(() => setLoading(false));
        }
    }, [user]);

    const leaveEvent = (eventId) => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            setError("Token is missing. Please log in again.");
            return;
        }

        axios
            .delete('/api/events/leave', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                data: { userId: user.id, eventId },
            })
            .then(() => {
                setProfile((prevProfile) => ({
                    ...prevProfile,
                    events: prevProfile.events.filter((event) => event.id_event !== eventId),
                }));
            })
            .catch(() => {
                alert('Failed to leave event');
            });
    };

    if (!user) {
        return <div>You are not authorized</div>;
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="profile-container">
            <h1>Profile</h1>
            <p><strong>Name:</strong> {profile?.name}</p>
            <p><strong>Surname:</strong> {profile?.surname}</p>
            <p><strong>Email:</strong> {profile?.email}</p>

            <h2>Events</h2>
            {profile?.events && profile?.events.length > 0 ? (
                <ul>
                    {profile.events.map((event) => (
                        <li key={event.id_event}>
                            <p>{event.name} - {new Date(event.date).toLocaleDateString()}</p>
                            <button onClick={() => leaveEvent(event.id_event)}>Leave</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You are not participating in any events</p>
            )}

            <button className="logout-btn" onClick={logoutUser}>Log out</button>
        </div>
    );
};

export default ProfilePage;
