import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './EventDetailPage.css';
import backgroundImage from '../images/background.jpg';

const EventDetailPage = () => {
    const { eventId } = useParams();
    const navigate = useNavigate();
    const [event, setEvent] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [serverError, setServerError] = useState('');
    const [userId, setUserId] = useState(null);
    const [isAdmin, setIsAdmin] = useState(false);

    useEffect(() => {
        // ���������� ���� ������������
        const fetchUserRole = async () => {
            const token = localStorage.getItem('authToken');

            if (!token) {
                setIsAdmin(false);
                setUserId(null);
                return;
            }

            try {
                const response = await fetch('http://localhost:5000/api/user-role', {
                    method: 'GET',
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                const data = await response.json();
                if (response.ok) {
                    setUserId(data.userId);
                    setIsAdmin(data.role === 'admin'); // ��������������, ��� ������ ���������� ���� "admin" ��� "user"
                } else {
                    console.error(data.message || 'Error fetching user role');
                    setUserId(null);
                    setIsAdmin(false);
                }
            } catch (error) {
                console.error('Error fetching user role:', error);
                setUserId(null);
                setIsAdmin(false);
            }
        };

        fetchUserRole();
    }, []);

    useEffect(() => {
        const fetchEvent = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/events/${eventId}`);
                const data = await response.json();
                if (response.ok) {
                    setEvent(data);
                } else {
                    setServerError(data.message || 'Error fetching event details');
                }
            } catch (error) {
                console.error('Error fetching event:', error);
                setServerError('Server error. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvent();
    }, [eventId]);

    const handleRegisterClick = async () => {
        if (!userId) {
            alert('Please log in first!');
            return;
        }

        try {
            const response = await fetch(`http://localhost:5000/api/events/${eventId}/register`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userId }), // �������� userId � �������
            });

            const data = await response.json();
            if (response.ok) {
                alert(data.message);
                setEvent((prevEvent) => ({
                    ...prevEvent,
                    participants: [...(prevEvent.participants || []), { id: userId, name: 'Your Name' }],
                }));
            } else {
                alert(data.message || 'Registration failed');
            }
        } catch (error) {
            console.error('Error registering for event:', error);
            alert('Server error during registration');
        }
    };

    const handleEditClick = () => {
        navigate(`/events/${eventId}/edit`);
    };

    const handleDeleteClick = async () => {
        if (window.confirm('Are you sure you want to delete this event?')) {
            try {
                const response = await fetch(`http://localhost:5000/api/events/${eventId}`, {
                    method: 'DELETE',
                });

                if (response.ok) {
                    alert('Event deleted successfully!');
                    navigate('/event');
                } else {
                    const data = await response.json();
                    alert(data.message || 'Failed to delete event');
                }
            } catch (error) {
                console.error('Error deleting event:', error);
                alert('Server error during deletion');
            }
        }
    };

    if (isLoading) {
        return <p>Loading event details...</p>;
    }

    return (
        <div className="background" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="event-detail-container">
                {serverError && <p className="error">{serverError}</p>}

                {event ? (
                    <div className="event-detail">
                        <button className="back-button" onClick={() => window.history.back()}>Back</button>

                        <img src={event.image} alt={event.name} className="event-image" />
                        <div className="event-info">
                            <h1>{event.name}</h1>
                            <p>{event.description}</p>
                            <p>Date: {new Date(event.date).toLocaleDateString()}</p>
                            <p>Place: {event.place_name}</p>
                            <p>Category: {event.category_name}</p>

                            <h3>Participants:</h3>
                            <ul>
                                {event.participants && event.participants.length > 0 ? (
                                    event.participants.map((participant, index) => (
                                        <li key={index}>
                                            {participant.name} {participant.surname}
                                        </li>
                                    ))
                                ) : (
                                    <p>No participants yet.</p>
                                )}
                            </ul>

                            {isAdmin ? (
                                <>
                                    <button onClick={handleEditClick}>Edit Event</button>
                                    <button onClick={handleDeleteClick}>Delete Event</button>
                                </>
                            ) : (
                                <button onClick={handleRegisterClick}>Register for Event</button>
                            )}
                        </div>
                    </div>
                ) : (
                    <p>Event not found</p>
                )}
            </div>
        </div>
    );
};

export default EventDetailPage;
